mapper.select:
SELECT DISTINCT T.FUGITIVEID
      FROM MODI_R2CGL_FUGITIVE_RSER T
     WHERE TO_CHAR(T.INSERTTIME, 'yyyy-MM-dd') = #{date,jdbcType=VARCHAR}

SELECT t.id as "id", T.FUGITIVENO AS "fugitiveNo", T.FUGITIVEID AS "fugitiveId", T.FUGITIVENAME AS "fugitiveName", T.ISARCHIVED AS "isArchived", T.ARCHIVEDREASON AS "archivedReason" FROM MODI_R2CGL_FUGITIVE T WHERE T.FUGITIVEID IN
#{item,jdbcType=VARCHAR}
AND t.ISARCHIVED='0'

SELECT T.RELATIONSHIP, COUNT(*) AS COUNT_D
  FROM MODI_R2CGL_FUGITIVE_RSER T
 WHERE TO_CHAR(T.INSERTTIME, 'yyyy-MM-dd') =#{date,jdbcType=VARCHAR}
   AND T.FUGITIVEID =#{fugitiveId,jdbcType=VARCHAR}
 GROUP BY T.RELATIONSHIP
 ORDER BY COUNT_D
